	private char[] replaceString(char[] val, String newString, int start) {

		for (int i = 0; i < val.length; i++) {
			if (i >= start) {
				if ((i - start) <= newString.length() - 1) {
					val[i] = newString.charAt(i - start);
				} else {
					break;
				}
			}
		}
		return val;
	}

