		// LET l_hist_text = " "
		// LET l_hist_text = "Parameter prorata charged for deactivation"
		histText = " Parameter prorata charged for deactivation";
		//
		// CALL History_InsMsisdn(p_subscriber_id, p_msisdn_no, l_hist_code,
