		// EXIT WHILE
		//
		// WHEN g_status_class != 0
		//
		// CALL Error_Append("", l_function)
		// EXIT WHILE
		// END CASE
		//
		// CALL History_InsSubscriber(p_subscriber_id, 0, p_histcode, l_hval,
