		// IF g_status_class != 0 THEN
		//
		// -- Problem in d_tmp_services_list_open
		// CALL Error_Append("", l_function)
		// EXIT WHILE
		// END IF
		//
		// -- Loop round getting the data
		// WHILE TRUE
		// -- Fetch the details
		// CALL d_tmp_services_list_fetch()
		// RETURNING l_sim.*, l_srm.*, l_tsa.*

