				logger.error("Exception getting the PFS record for subscriber: "
						+ record.getSubscriber_id() + " EXCEPTION: " + e);
				// throw new EPPIXSeriousException("2",
				// "Exception getting the PFS record for subscriber: " +
				// subscriberId + " EXCEPTION: " + e);
