		//
		// {
		// IF g_type = "RETAIL" THEN
		// -- Retrieve the mch_migr_chrge_hdr record
		// CALL d_mch_get("RETAIL", "", p_new_tariff,
		// l_migration_no)
		// RETURNING l_mch.*
		// ELSE
		// CALL d_mch_get("TARIFF", "", p_new_nm.nm_network_tariff,
