					// OR
					// l_mcd.mcd_contract_term = "B") THEN
					// -- Get the charge code details
					// LET l_chg_date = NULL
					chgDate = new Date();
					//
					// CALL Charge_Get (l_mcd.mcd_charge_code, "", "",
