				vamDmo = (VamActiveMsisdnDMO) vamIt.next();
				// IF g_status_class != 0 THEN
				// IF g_status_class = 100 THEN
				// CALL Error_Reset()
				//
				// EXIT WHILE
				// ELSE
				// LET l_error = "Call to fetch d_sim_msi_list failed : "
				//
				// CALL Error_Append(l_error, l_function)
				//
				// EXIT WHILE
				// END IF
				// END IF
